  const [message] = React.useState([
    {id:'001',title:'消息1',content:'锄禾日当午'},
    {id:'002',title:'消息1',content:'锄禾日当午'},
    {id:'003',title:'消息3',content:'锄禾日当午'},
    {id:'004',title:'消息4',content:'锄禾日当午'},
  ]);

  return (
    <ul>
      <li>
        <Link to="/message1">Message1</Link>&nbsp;&nbsp;
      </li>
    </ul>
  );
}
