import React from "react";
import { NavLink, Routes, Route, Navigate,useRoutes } from "react-router-dom";
import About from "./Pages/About";
import Home from "./Pages/Home";

export default function App() {
// 路由表
const element = useRoutes([
{
  path:'/a'
}

])



  function computedClassName({ isActive }) {
    return isActive ? "list-group-item cdx" : "list-group-item";
  }
  return (
    <div>
      <div className="row">
        <div className="col-xs-offset-2 col-xs-8">
          <div className="page-header">
            <h2>React Router Demo</h2>
          </div>
        </div>
      </div>
      <div className="row">
        <div className="col-xs-2 col-xs-offset-2">
          <div className="list-group">
            {/* 路由链接 */}
            <NavLink className={computedClassName} to="/about">
              About
            </NavLink>
            <NavLink className={computedClassName} to="/home">
              Home
            </NavLink>

            {/* <a className="list-group-item active" href="./about.html">
              About
            </a> */}
            {/* <a className="list-group-item" href="./home.html">
              Home
            </a> */}
          </div>
        </div>
        <div className="col-xs-6">
          <div className="panel">
            <div className="panel-body">
              <Routes>
                <Route path="/about" element={<About />} />
                <Route path="/home" element={<Home />} />
                <Route path="/" element={<Navigate to="/about" />} />
              </Routes>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
