import React, { Component } from 'react'

export default  class count extends Component {
state = {count:0}
add = () => {
  this.setState({count:this.state.count+1})
}
  render() {
    return (
      <div>
          <h2>当前求和为:{this.state.count}</h2>
          <button onClick={this.add}>点我加一</button>


      </div>
    )
  }
}
